const showfavorites = async function(storefavorites) {
    console.log(storefavorites);

    const favoriteGames = [];

    for (let slug of storefavorites) {
        try {
            const response = await fetch(`${baseUrl}/${slug}?key=${apiKey}`);
            const game = await response.json();
            favoriteGames.push({
                title: game.name,
                slug: game.slug, 
                rating: game.rating,
                metacritic: game.metacritic,
                released: game.released,
                background_image: game.background_image
            });
        } catch (error) {
            console.error('Error fetching favorite game:', error);
        }
    }

    if (favoriteGames.length > 0) {
        showGames(favoriteGames);
    } else {
        console.log('No favorite games to display.');
    }
};




